import logo from './logo.svg';
import './App.css';
import { useState } from "react";

function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9],['+','-','*','%', '=']];
  const [Mode, setMode] = useState('Number');
  let number= 0;
  const [equation,setEquation]= useState('0');



  const checkPin = (pin) => {

    if(pin === '='){
    }
    else if (['+', '-', '*', '%'].includes(pin)) {
      if(Mode === 'Number'){
        setEquation(equation + pin)
        setMode('Operator')
      }
      else{
        setEquation(equation.slice(0, -1) + pin)
      }
    }

    else if (pin>=1 && pin<=9) {
        setMode('Number')
        number = number * 10 + pin;
        equation === '0'? setEquation(number) : setEquation(equation + String(number))
    }


  }

  return (
    <div>
      <h1>Calculator App</h1>
      <div>{equation}</div>
      {buttons.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span class="buttonBox" key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
    </div>
  );
}

export default App;
