import logo from './logo.svg';
import './App.css';
import { useState } from "react";

function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9],['+', '-', '*', '%', '=']];
  const [currentOperator, setOperator] = useState(null);
  const [display, setDisplay] = useState(0);
  const [sum, setSum] = useState(0);
  const [degree, setDegree] = useState(1);

  const calculate = (operand1, operator, operand2) => {
    let result;
    switch (operator) {
      case '+':
        result = operand1 + operand2;
        break;
      case '-':
        result = operand1 - operand2;
        break;
      case '*':
        result = operand1 * operand2;
        break;
      case '%':
        result = operand1 / operand2;
        break;
      default:
        result = operand2;
    }
    return result;
  }

  const checkPin = (pin) => {
    if (pin >= 1 && pin <= 9) {
      if (currentOperator !== null) {
        const newSum = calculate(sum, currentOperator, display);
        setSum(newSum);
        setDisplay(0);
        setOperator(null);
      } else {
        setDisplay(prevDisplay => prevDisplay === 0 ? pin : prevDisplay * 10 + pin);
      }
    } else if (pin === '=') {
      const newSum = calculate(sum, currentOperator, display);
      setSum(newSum);
      setDisplay(newSum);
      setOperator(null);
    } else if (['+', '-', '*', '%'].includes(pin)) {
      setSum(display);
      setDisplay(0);
      setOperator(pin);
    }
  }

  return (
    <div>
      <h1>Calculator App</h1>
      <div>{display}</div>
      {buttons.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
    </div>
  );
}

export default App;
