import logo from './logo.svg';
import './App.css';
import { useState } from "react";

function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9],['+','-','*','%']];
  const [currentMode, setMode] = useState('numberInputing');
  const [display, setDisplay] = useState(0);
  const [sum, setSum] = useState(0);
  const [degree, setDegree] = useState(1);

  const checkPin = (pin) => {

    if (pin>=1 && pin<=9) {
      if(currentMode === 'Operatoring'){
        setMode('NumberInputing')
        setSum(display)
        setDisplay(0)
      }
      else if(currentMode === 'Resulting'){
        setMode('NumberInputing')
        setDisplay(0)
      }

      setDisplay(prevDisplay => prevDisplay === 0 ? pin : prevDisplay * 10 + pin);
      setDegree(degree + 1);
    }
  }

  return (
    <div>
      <h1>Calculator App</h1>
      <div>{display}</div>
      {buttons.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
    </div>
  );
}

export default App;
