import logo from './logo.svg';
import './App.css';
import { useState } from "react";

function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9],['+','-','*','%', '=']];
  const [currentOperator, setOperator] = useState(null);
  const [display, setDisplay] = useState(0);
  const [sum, setSum] = useState(0);
  const [degree, setDegree] = useState(1);

  const calculate = ()=>{
    //after a '=' has been pressed or another set of number has started
    //will perform calculate between the latest sum(calculating the last two of number set)
    if(currentOperator === '+')
    {
      setSum(prevSum => prevSum + display);
    }
    else if(currentOperator === '-')
    {
      setSum(prevSum =>prevSum - display);
    }
    else if(currentOperator === '*')
    {
      setSum(prevSum =>prevSum * display)
    }
    else if(currentOperator === '%')
    {
      setSum(prevSum =>prevSum / display)
    }
    return sum;
  }

  const checkPin = (pin) => {

    if (pin>=1 && pin<=9) {
      if(currentOperator !== null){
        // ทดในใจ เอาสองพจน์ก่อนหน้านี้ไปบวกลบกันก่อน แล้วเปลี่ยนแสดงผลเป็น 0 เพื่อให้นับค่าใหม่ที่กรอกมาได้
        calculate()
        setDisplay(0)
      }

        setDisplay(prevDisplay => prevDisplay === 0 ? pin : prevDisplay * 10 + pin);
        setDegree(degree + 1);
    }
    else if(pin === '='){
      calculate()
      setDisplay(sum)
      setOperator(null)
    }
    else if (['+', '-', '*', '%'].includes(pin)) {
      setOperator(pin);
    }


  }

  return (
    <div>
      <h1>Calculator App</h1>
      <div>{display} {sum} {currentOperator}</div>
      {buttons.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
    </div>
  );
}

export default App;
