import logo from './logo.svg';
import './App.css';
import { useState } from "react";

function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9],['+','-','*','%', '=']];
  //const [currentOperator, setOperator] = useState(null);
  const [Mode, setMode]= useState('isNumber')
  const [display, setDisplay] = useState(0);
  const [sum, setSum] = useState(0);
  const [degree, setDegree] = useState(1);

  const calculate = (currentOperator)=>{
    //after a '=' has been pressed or another set of number has started
    //will perform calculate between the latest sum(calculating the last two of number set)
    if(currentOperator === '+')
    {
      setSum(sum + display);
    }
    else if(currentOperator === '-')
    {
      setSum(sum - display);
    }
    else if(currentOperator === '*')
    {
      setSum(sum * display)
    }
    else if(currentOperator === '%')
    {
      setSum(sum / display)
    }
  }

  const checkPin = (pin) => {

    if(pin === '='){
      calculate()
      setDisplay(sum)
    }
    else if (['+', '-', '*', '%'].includes(pin)) {
      if(Mode ==='isNumber'){
        calculate(pin)
        setDisplay(0)
        setMode('isOperand')
      }
    }

    else if (pin>=1 && pin<=9) {
        setDisplay(prevDisplay => prevDisplay === 0 ? pin : prevDisplay * 10 + pin);
        setDegree(degree + 1);
        setMode('isNumber')
    }


  }

  return (
    <div>
      <h1>Calculator App</h1>
      <div>{display} {sum}</div>
      {buttons.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span class="buttonBox" key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
    </div>
  );
}

export default App;
