import logo from './logo.svg';
import './App.css';
import { useState } from "react";
import { evaluate } from 'mathjs';


function App() {
  const buttonsNum = [[1,2,3],[4,5,6],[7,8,9]];
  const [Mode, setMode] = useState('Number');
  let number= 0;
  const [equation,setEquation]= useState('0');



  const checkPin = (pin) => {
    const calculate = () => {
      try {
        return evaluate(equation).toString();
      } catch (error) {
        return 'Error';
      }
    };

      if(pin === '='){
      setEquation(calculate());
    }
    else if (['+', '-', '*', '%'].includes(pin)) {
      if(Mode === 'Number'){
        setEquation(equation + pin)
        setMode('Operator')
      }
      else{
        setEquation(equation.slice(0, -1) + pin)
      }
    }

    else if (pin>=1 && pin<=9) {
        setMode('Number')
        number = number * 10 + pin;
        equation === '0'? setEquation(number) : setEquation(equation + String(number))
    }


  }

  return (
    <div class="displayBox">
      <h1>Calculator App</h1>
      <div >{equation}</div>
      {buttonsNum.map((buttonRow, rowIndex) => (
        <div key={rowIndex}>
          {buttonRow.map((pin) => (
            <span class="buttonBox" key={pin} onClick={() => checkPin(pin)}>{pin}</span>
          ))}
        </div>
      ))}
      <span>adssad</span>
    </div>
  );
}

export default App;
