import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from "react";


function App() {
  const buttons = [[1,2,3],[4,5,6],[7,8,9]];
  const [currentMode, setMode]= useState('numberInput');
  const sum = 0;
  const [display,setDisplay] = useState(0);
  const degree = 0;

  const checkPin =(pin) =>{
    if(currentMode = 'numberInput')
      {
        display == 0? setDisplay(pin) : setDisplay(display*Math.pow(10,degree) + pin);
      }
  }
  return (
    <div>
      <h1>Calculator App</h1>
      {display}
      {buttons.map((buttonRow, rowIndex)=>( 
        <div key={rowIndex}>
        {buttonRow.map((pin)=>(  
          <span key= {pin} onClick={()=>{checkPin(pin)}}>{pin}</span>
        ))}
        </div>
      ))}
     </div>
  );
}

export default App;
